import React, { useState } from 'react';
import { Loader, CheckCircle, XCircle } from 'lucide-react';
import { useQuiz } from '../hooks/useQuiz';
import Button from '../components/common/Button';
import Input from '../components/common/Input';

const CreatePage = ({ setCurrentPage }) => {
  const [step, setStep] = useState('input');
  const [topic, setTopic] = useState('');
  const [numQuestions, setNumQuestions] = useState(5);
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [userAnswers, setUserAnswers] = useState([]);
  const [results, setResults] = useState(null);

  const { currentQuiz, generateQuiz, checkQuiz, isLoading } = useQuiz();

  const handleGenerateQuiz = async () => {
    if (!topic.trim()) return;
    
    setStep('generating');
    try {
      const quiz = await generateQuiz(topic, numQuestions);
      setUserAnswers(new Array(quiz.questions.length).fill(null));
      setStep('quiz');
    } catch (error) {
      alert('Ошибка генерации: ' + error.message);
      setStep('input');
    }
  };

  const selectAnswer = (answerIndex) => {
    const newAnswers = [...userAnswers];
    newAnswers[currentQuestion] = answerIndex;
    setUserAnswers(newAnswers);
  };

  const handleCheckQuiz = async () => {
    try {
      const result = await checkQuiz(
        currentQuiz.id,
        currentQuiz.topic,
        currentQuiz.category,
        currentQuiz.questions,
        userAnswers
      );
      setResults(result);
      setStep('results');
    } catch (error) {
      alert('Ошибка проверки: ' + error.message);
    }
  };

  const resetQuiz = () => {
    setStep('input');
    setTopic('');
    setNumQuestions(5);
    setCurrentQuestion(0);
    setUserAnswers([]);
    setResults(null);
  };

  if (step === 'input') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 py-12">
        <div className="max-w-4xl mx-auto px-6">
          <div className="bg-white rounded-2xl shadow-lg p-10">
            <h2 className="text-3xl font-bold text-gray-900 mb-8">Создать новую викторину</h2>
            
            <div className="space-y-6">
              <Input
                type="text"
                label="Тема викторины"
                placeholder="Например: Математика 5 класс, История России, Физика..."
                value={topic}
                onChange={(e) => setTopic(e.target.value)}
              />

              <div>
                <label className="block text-sm font-semibold text-gray-700 mb-2">
                  Количество вопросов
                </label>
                <div className="grid grid-cols-3 gap-4">
                  {[5, 10, 20].map(num => (
                    <button
                      key={num}
                      onClick={() => setNumQuestions(num)}
                      className={`py-3 rounded-xl font-semibold transition ${
                        numQuestions === num
                          ? 'bg-yellow-500 text-white'
                          : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                      }`}
                    >
                      {num} вопросов
                    </button>
                  ))}
                </div>
              </div>

              <Button
                onClick={handleGenerateQuiz}
                disabled={!topic.trim()}
                variant="primary"
                size="lg"
                fullWidth
              >
                Создать викторину
              </Button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (step === 'generating') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 py-12">
        <div className="max-w-4xl mx-auto px-6">
          <div 
            className="rounded-2xl shadow-lg p-12 text-center text-white"
            style={{ backgroundColor: currentQuiz?.style?.color || '#6b7280' }}
          >
            <Loader className="w-16 h-16 animate-spin mx-auto mb-6" />
            <h2 className="text-3xl font-bold mb-4">Генерируем викторину...</h2>
            <p className="text-xl opacity-90">ИИ создает вопросы по теме: {topic}</p>
          </div>
        </div>
      </div>
    );
  }

  if (step === 'quiz' && currentQuiz) {
    const question = currentQuiz.questions[currentQuestion];
    const progress = ((currentQuestion + 1) / currentQuiz.questions.length) * 100;

    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 py-12">
        <div className="max-w-4xl mx-auto px-6">
          <div className="mb-6">
            <div className="flex justify-between text-sm text-gray-600 mb-2">
              <span>Вопрос {currentQuestion + 1} из {currentQuiz.questions.length}</span>
              <span>{Math.round(progress)}%</span>
            </div>
            <div className="w-full bg-gray-200 rounded-full h-2">
              <div 
                className="h-2 rounded-full transition-all duration-300"
                style={{ width: `${progress}%`, backgroundColor: currentQuiz.style.color }}
              />
            </div>
          </div>

          <div className="bg-white rounded-2xl shadow-lg p-10 mb-6">
            <div className="flex items-start space-x-4 mb-8">
              <span className="text-4xl">{currentQuiz.style.icon}</span>
              <div className="flex-1">
                <span className="text-sm font-semibold text-gray-500 uppercase">
                  {currentQuiz.category}
                </span>
                <h2 className="text-2xl font-bold text-gray-900 mt-2">{question.question}</h2>
              </div>
            </div>

            <div className="space-y-3">
              {question.options.map((option, idx) => (
                <button
                  key={idx}
                  onClick={() => selectAnswer(idx)}
                  className={`w-full text-left p-4 rounded-xl border-2 transition ${
                    userAnswers[currentQuestion] === idx
                      ? 'border-yellow-500 bg-yellow-50'
                      : 'border-gray-200 hover:border-gray-300'
                  }`}
                >
                  <div className="flex items-center space-x-3">
                    <div className={`w-6 h-6 rounded-full border-2 flex items-center justify-center ${
                      userAnswers[currentQuestion] === idx
                        ? 'border-yellow-500 bg-yellow-500'
                        : 'border-gray-300'
                    }`}>
                      {userAnswers[currentQuestion] === idx && (
                        <div className="w-2 h-2 bg-white rounded-full" />
                      )}
                    </div>
                    <span className="text-gray-900">{option}</span>
                  </div>
                </button>
              ))}
            </div>
          </div>

          <div className="flex justify-between">
            <Button
              onClick={() => setCurrentQuestion(prev => Math.max(0, prev - 1))}
              disabled={currentQuestion === 0}
              variant="secondary"
            >
              Назад
            </Button>
            
            {currentQuestion < currentQuiz.questions.length - 1 ? (
              <Button onClick={() => setCurrentQuestion(prev => prev + 1)} variant="primary">
                Далее
              </Button>
            ) : (
              <Button
                onClick={handleCheckQuiz}
                disabled={userAnswers.includes(null)}
                variant="success"
              >
                Проверить
              </Button>
            )}
          </div>
        </div>
      </div>
    );
  }

  if (step === 'results' && results) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100 py-12">
        <div className="max-w-4xl mx-auto px-6">
          <div className="bg-white rounded-2xl shadow-lg p-10 mb-6">
            <div className="text-center mb-8">
              <div className={`text-6xl font-bold mb-4 ${
                results.score >= 70 ? 'text-green-500' : 'text-red-500'
              }`}>
                {results.score}%
              </div>
              <h2 className="text-2xl font-bold text-gray-900 mb-2">
                {results.score >= 90 ? 'Отлично!' : results.score >= 70 ? 'Хорошо!' : 'Попробуйте еще раз'}
              </h2>
              <p className="text-gray-600">
                Правильных ответов: {results.correctAnswers} из {results.totalQuestions}
              </p>
            </div>

            <div className="space-y-4">
              {results.results.map((result, idx) => (
                <div key={idx} className={`p-4 rounded-xl border-2 ${
                  result.isCorrect ? 'border-green-200 bg-green-50' : 'border-red-200 bg-red-50'
                }`}>
                  <div className="flex items-start space-x-3">
                    {result.isCorrect ? (
                      <CheckCircle className="w-6 h-6 text-green-500 mt-1 flex-shrink-0" />
                    ) : (
                      <XCircle className="w-6 h-6 text-red-500 mt-1 flex-shrink-0" />
                    )}
                    <div className="flex-1">
                      <p className="font-semibold text-gray-900 mb-2">{result.question}</p>
                      <p className="text-sm text-gray-600">
                        Ваш ответ: <span className={result.isCorrect ? 'text-green-600' : 'text-red-600'}>
                          {result.options[result.userAnswer]}
                        </span>
                      </p>
                      {!result.isCorrect && (
                        <p className="text-sm text-gray-600 mt-1">
                          Правильный ответ: <span className="text-green-600">
                            {result.options[result.correctAnswer]}
                          </span>
                        </p>
                      )}
                    </div>
                  </div>
                </div>
              ))}
            </div>

            <Button onClick={resetQuiz} variant="primary" size="lg" fullWidth className="mt-8">
              Создать новую викторину
            </Button>
          </div>
        </div>
      </div>
    );
  }

  return null;
};

export default CreatePage;
