import React, { useState } from 'react';
import { Home, PlusCircle, LogIn } from 'lucide-react';
import AuthModal from '../auth/AuthModal';
import { useAuth } from '../../hooks/useAuth';

const Header = ({ currentPage, setCurrentPage }) => {
  const [isAuthModalOpen, setIsAuthModalOpen] = useState(false);
  const { user, logout } = useAuth();

  return (
    <>
      <header className="bg-white border-b border-gray-200 shadow-sm sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-2 cursor-pointer" onClick={() => setCurrentPage('home')}>
              <h1 className="text-3xl font-bold">
                <span className="text-gray-800">Quiz</span>
                <span className="text-yellow-500">Grad</span>
              </h1>
            </div>
            <nav className="flex items-center space-x-6">
              <button
                onClick={() => setCurrentPage('home')}
                className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition ${
                  currentPage === 'home' 
                    ? 'bg-gray-100 text-gray-900' 
                    : 'text-gray-600 hover:text-gray-900 hover:bg-gray-50'
                }`}
              >
                <Home size={20} />
                <span>Home</span>
              </button>

              <button
                onClick={() => setCurrentPage('create')}
                className={`flex items-center space-x-2 px-4 py-2 rounded-lg transition ${
                  currentPage === 'create' 
                    ? 'bg-gray-100 text-gray-900' 
                    : 'text-gray-600 hover:text-gray-900 hover:bg-gray-50'
                }`}
              >
                <PlusCircle size={20} />
                <span>Create</span>
              </button>

              {user ? (
                <div className="flex items-center space-x-4">
                  <span className="text-sm text-gray-600">
                    Привет, <span className="font-semibold">{user.username}</span>
                  </span>
                  <button
                    onClick={logout}
                    className="px-4 py-2 text-gray-600 hover:text-gray-900 rounded-lg transition"
                  >
                    Выйти
                  </button>
                </div>
              ) : (
                <button
                  onClick={() => setIsAuthModalOpen(true)}
                  className="flex items-center space-x-2 px-4 py-2 text-gray-600 hover:text-gray-900 rounded-lg transition hover:bg-gray-50"
                >
                  <LogIn size={20} />
                  <span>Login</span>
                </button>
              )}
            </nav>
          </div>
        </div>
      </header>

      <AuthModal 
        isOpen={isAuthModalOpen} 
        onClose={() => setIsAuthModalOpen(false)} 
      />
    </>
  );
};

export default Header;
